import * as React from "react"
import { cn } from "@/lib/utils"
import { ZEN_COLORS, ZEN_SPACING } from "@/components/ui/zen/tokens"

const ZenCard = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & {
    variant?: "default" | "outline" | "ghost"
    padding?: "none" | "sm" | "md" | "lg"
  }
>(({ className, variant = "default", padding = "md", ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "rounded-lg border text-card-foreground shadow-sm",
      ZEN_COLORS.card[variant].bg,
      ZEN_COLORS.card[variant].border,
      ZEN_SPACING.card[padding],
      className
    )}
    {...props}
  />
))
ZenCard.displayName = "ZenCard"

const ZenCardHeader = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "flex flex-col space-y-1.5",
      ZEN_SPACING.card.header,
      className
    )}
    {...props}
  />
))
ZenCardHeader.displayName = "ZenCardHeader"

const ZenCardTitle = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLHeadingElement>
>(({ className, ...props }, ref) => (
  <h3
    ref={ref}
    className={cn(
      "text-lg font-semibold leading-none tracking-tight",
      ZEN_COLORS.card.title,
      className
    )}
    {...props}
  />
))
ZenCardTitle.displayName = "ZenCardTitle"

const ZenCardDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => (
  <p
    ref={ref}
    className={cn(
      "text-sm text-muted-foreground",
      ZEN_COLORS.card.description,
      className
    )}
    {...props}
  />
))
ZenCardDescription.displayName = "ZenCardDescription"

const ZenCardContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      ZEN_SPACING.card.content,
      className
    )}
    {...props}
  />
))
ZenCardContent.displayName = "ZenCardContent"

const ZenCardFooter = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "flex items-center",
      ZEN_SPACING.card.footer,
      className
    )}
    {...props}
  />
))
ZenCardFooter.displayName = "ZenCardFooter"

export {
  ZenCard,
  ZenCardHeader,
  ZenCardFooter,
  ZenCardTitle,
  ZenCardDescription,
  ZenCardContent
}
